DELIMITER ;;
CREATE PROCEDURE spAddAccount(
	IN pAccountID nvarchar(50), 
	IN pPasswordHash nvarchar(256),
    IN pSalt nvarchar(256),
    IN pEmployeeID nvarchar(10)
)
BEGIN
	INSERT INTO Library.Account (AccountID, PasswordHash, Salt, EmployeeID)
	VALUES (pAccountID, sha2(CONCAT(pPasswordHash, pSalt), 512), pSalt, pEmployeeID);
END ;;
