DELIMITER ;;
CREATE PROCEDURE spLogin(
	IN pAccountID nvarchar(50),
    IN pPassword nvarchar(50),
    OUT pOutValue int
)
BEGIN
	select Count(AccountID)
    into pOutValue
    from Account
    where AccountID = pAccountID AND sha2(CONCAT(pPassword, Salt), 512) = PasswordHash
    limit 1;
END ;;
