CREATE PROCEDURE spUpdatePassword(
	IN pAccountID nvarchar(50),
	IN pOldPassword nvarchar(50),
    IN pNewPassword nvarchar(50),
	OUT pOutValue int
)
BEGIN
	DECLARE pSalt nvarchar(256);
    DECLARE pHash nvarchar(256);
    
    select Salt into pSalt from Account WHERE AccountID = pAccountID;
    select PasswordHash into pHash from Account WHERE AccountID = pAccountID; 
    
	if(sha2(CONCAT(pOldPassword, pSalt), 512) = pHash) 
		then
			Update Account 
			Set PasswordHash = sha2(CONCAT(pNewPassword, Salt), 512)
			WHERE AccountID = pAccountID;
			
			Set pOutValue = 1;
		else
			Set pOutValue = 0;
	end if;
END ;;
